mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM(
		SELECT
		board_no, title, content, writer_id, writer_nick, hit, write_date FROM board
		ORDER BY board_no DESC
	) B
ORDER BY rnum
)
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE board
SET hit=hit+1
WHERE board_no=#{board_no}

UPDATE board
SET content= #{content},
title = #{title}
WHERE board_no = #{board_no}
